import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import java.text.DecimalFormat
import java.text.DecimalFormatSymbols
import java.util.Locale

def flowFile = session.get()
if (!flowFile) return

def usdToEur = flowFile.getAttribute('usd_to_eur')?.toDouble() ?: 1.0
def gbpToEur = flowFile.getAttribute('gbp_to_eur')?.toDouble() ?: 1.0
def eurToEur = flowFile.getAttribute('eur_to_eur')?.toDouble() ?: 1.0

// Use DecimalFormat with US-style decimal point
def symbols = new DecimalFormatSymbols(Locale.US)
def df = new DecimalFormat("#.###", symbols)

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def lines = IOUtils.readLines(inputStream, StandardCharsets.UTF_8)
    if (lines.isEmpty()) return

    // Parse header and columns
    def header = lines[0].trim()
    def columns = header.split(',')

    // Find indexes for required columns
    def currencyIndex = columns.findIndexOf { it == "currency" }
    def amountIndex = columns.findIndexOf { it == "total_amount" }
    def totalEurIndex = columns.findIndexOf { it == "total_eur" }

    if (totalEurIndex == -1) {
        throw new Exception("Column 'total_eur' not found in CSV header")
    }

    // Write the header as is (no change)
    outputStream.write((header + "\n").getBytes(StandardCharsets.UTF_8))

    // Process each data line
    lines[1..-1].each { line ->
        def fields = line.split(',')

        while (fields.length < columns.length) {
            fields += ""
        }

        def currency = fields[currencyIndex]?.trim()?.toLowerCase() ?: ""
        def totalAmountStr = fields[amountIndex]?.trim() ?: "0"
        def totalAmount = 0.0
        try {
            totalAmount = totalAmountStr.toDouble()
        } catch (Exception e) {
            totalAmount = 0.0
        }

        def rate = 1.0
        switch (currency) {
            case 'usd':
                rate = usdToEur
                break
            case 'gbp':
                rate = gbpToEur
                break
            case 'eur':
                rate = eurToEur
                break
            default:
                rate = 1.0
        }

        def totalEur = totalAmount * rate
        def totalEurStr = df.format(totalEur)  // Ensures dot as decimal separator

        fields[totalEurIndex] = totalEurStr

        outputStream.write((fields.join(',') + "\n").getBytes(StandardCharsets.UTF_8))
    }

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

def flowFile = session.get()
if (!flowFile) return

try {
    // Get the rates as Double from attributes (if missing, default to 1.0)
    def rateUsd = flowFile.getAttribute('rate_usd')?.toDouble() ?: 1.0
    def rateGbp = flowFile.getAttribute('rate_gbp')?.toDouble() ?: 1.0

    // Calculate inverse rates (1 / rate)
    def usdToEur = 1.0 / rateUsd
    def gbpToEur = 1.0 / rateGbp
    def eurToEur = 1.0  // EUR to EUR is always 1

 // Round to 3 decimals with dot as decimal separator
    usdToEur = String.format(Locale.US, '%.3f', usdToEur)
    gbpToEur = String.format(Locale.US, '%.3f', gbpToEur)
    eurToEur = String.format(Locale.US, '%.3f', eurToEur)

    // Set new attributes
    flowFile = session.putAttribute(flowFile, 'usd_to_eur', usdToEur.toString())
    flowFile = session.putAttribute(flowFile, 'gbp_to_eur', gbpToEur.toString())
    flowFile = session.putAttribute(flowFile, 'eur_to_eur', eurToEur.toString())

    // Transfer flowfile success
    session.transfer(flowFile, REL_SUCCESS)
} catch (Exception e) {
    log.error("Error calculating exchange rates", e)
    session.transfer(flowFile, REL_FAILURE)
}
